import {mapState,mapMutations} from "vuex";
export default {
    data() {
        return {
            text: '',
            childValue: '',
        }
       
    },
    props: {
        type: String,
        categoryId: String,
    },
    computed:{
        
    },
     methods: {
           goback(){
                 this.$router.go(-1);
           } ,
            onSearch(){
                console.log(this.text , this.type)
                if(this.type=='goodslist'){
                this.$axios.get("/goods/listGoods",{params:{
                 keyword:this.text,
                 }}).then(res=>{
                    console.log(res.data.data.list);
                    this.childValue=res.data.data.list;
                    this.$emit('searchText',this.childValue);
                    this.$emit('changeKeyWords',this.text);
                })
                }
                if(this.type=='searchlist'){
                     console.log(2)
                     this.$axios.get("/goods/listGoods",{params:{
                     categoryId:this.categoryId,
                     keyword:this.text,
                 }}).then(res=>{
                    console.log(res.data.data.list);
                    this.childValue=res.data.data.list;
                    this.$emit('searchText',this.childValue);
                    this.$emit('changeKeyWords',this.text);        
                })
                }
                if(this.type=='order'){
                     this.$axios.post("/order/listSearch",{
                     page:1,
                     limit: 100,
                     keywords:this.text,
                 }).then(res=>{
                    console.log(res);
                    this.childValue=res.data.data.list;
                    this.$emit('searchText',this.childValue);
                    this.$emit('changeKeyWords',this.text);        
                })
                }
             },
        },
        computed: {
     ...mapState(['searchShow']),
     },
}
